var Air=(function(){function fire(){render();}
function render(){var $form=$("#air-form");dragAndDropForm($form);$form.on("submit",onSubmitForm);}
function dragAndDropForm($form){if(isAdvancedUpload){$form.addClass('has-advanced-upload');$form.on('drag dragstart dragend dragover dragenter dragleave drop',function(e){e.preventDefault();e.stopPropagation();}).on('dragover dragenter',function(){$form.addClass('is-dragover');}).on('dragleave dragend drop',function(){$form.removeClass('is-dragover');}).on('drop',function(e){droppedFiles=e.originalEvent.dataTransfer.files;});}}
function onSubmitForm(e){e.preventDefault();callRecognition();return false;}
function callRecognition(){console.log("====================");var formData=new FormData();var imageInput=document.getElementsByName("air-file")[0];formData.append('air-file',imageInput.files[0]);var req=new XMLHttpRequest();req.open("POST","/ui/ext/ArtImageRecognition");req.onload=function(event){alert(event.target.responseText);};req.send(formData);}
return{fire:fire};})();var isAdvancedUpload=function(){var div=document.createElement('div');return(('draggable'in div)||('ondragstart'in div&&'ondrop'in div))&&'FormData'in window&&'FileReader'in window;}();